<template>
  <div class="contacts">
    <div class="contacts__title frame frame--title">
      <h1>Contacts</h1>
    </div>
    <div class="contacts__body frame">
      <ul class="contacts__list">
        <li class="contacts__item" v-for="item of items">
          <div class="contacts__icon" v-html="item.icon"></div>
          <span class="contacts__name">{{ item.name }}</span>
          <a class="contacts__label" :href="item.href" :target="item.target_blank ? '_blank' : '_self'">{{ item.label }}</a>
        </li>
      </ul>
    </div>
  </div>
</template>

